import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, Image } from 'react-native';
import { Entypo } from '@expo/vector-icons';
import styles from '../styles';

const HomeScreen = ({ navigation }) => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [listaPrecios, setListaPrecios] = useState([]);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleMenuOption = (screenName) => {
    setMenuOpen(false);
    navigation.navigate(screenName);
  };

  async function obtenerListaPrecios() {
    try {
      const response = await fetch('/lista_precios');
      const listaPrecios = await response.json();
      setListaPrecios(listaPrecios);
    } catch (error) {
      console.error('Error al obtener los datos de lista_precios:', error);
    }
  }

  useEffect(() => {
    obtenerListaPrecios();
  }, []);

  return (
    <View style={styles.container}>
      <View style={styles.menuContainer}>
        <TouchableOpacity onPress={toggleMenu}>
          <Entypo name={menuOpen ? 'cross' : 'menu'} size={24} color="white" style={styles.menuIcon} />
        </TouchableOpacity>
        <Text style={styles.menuText}>Men√∫</Text>
      </View>
      {menuOpen && (
        <View style={styles.dropdownMenu}>
          <TouchableOpacity style={styles.menuOption} onPress={() => handleMenuOption('IngredientList')}>
            <Text style={styles.menuOptionText}>Ver Ingredientes</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.menuOption} onPress={() => handleMenuOption('TortasScreen')}>
            <Text style={styles.menuOptionText}>Ver Tortas</Text>
          </TouchableOpacity>
        </View>
      )}

      <View style={styles.imageContainer}>
        <Image
          source={{ uri: 'https://www.accessatlanta.com/resizer/v3Dx3eAvkJIMx1E2jafsfc0j-a4=/1024x576/smart/cloudfront-us-east-1.images.arcpublishing.com/ajc/67CJDJRFP5EQ3HA6U4DKHZZHFA.jpg' }}
          style={styles.image}
          resizeMode="cover"
        />
      </View>
      
      <View style={styles.table}>
        <View style={styles.row}>
          <Text style={[styles.header, styles.cell]}>ID Torta</Text>
          <Text style={[styles.header, styles.cell]}>Nombre Torta</Text>
          <Text style={[styles.header, styles.cell]}>Costo Total</Text>
        </View>
        {listaPrecios.map((item) => (
          <View style={styles.row} key={item.id_torta}>
            <Text style={styles.cell}>{item.id_torta}</Text>
            <Text style={styles.cell}>{item.nombre_torta}</Text>
            <Text style={styles.cell}>{item.costo_total}</Text>
          </View>
        ))}
      </View>

      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.navigate('IngredientList')}
      >
        <Text style={styles.buttonText}>Ver Ingredientes</Text>
      </TouchableOpacity>
      <TouchableOpacity
        style={styles.button}
        onPress={() => navigation.navigate('TortasScreen')}
      >
        <Text style={styles.buttonText}>Ver Tortas</Text>
      </TouchableOpacity>
    </View>
  );
};

export default HomeScreen;
