import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet } from 'react-native';
import axios from 'axios';

const IngredientesScreen = () => {
  const [ingredientes, setIngredientes] = useState([]);

  const fetchIngredientes = async () => {
    try {
      const response = await axios.get('http://localhost:3000/ingredientes');
      console.log(response.data)
      setIngredientes(response.data);
    } catch (error) {
      console.error('Error al obtener los datos:', error);
    }
  };

  useEffect(() => {
    fetchIngredientes();
  }, []);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Listado de Ingredientes</Text>
      console.log(response.data)
      <View style={styles.ingredientesList}>
        {ingredientes.map(ingrediente => (
          <View style={styles.ingredienteItem} key={ingrediente.ID}>
            <Text style={styles.ingredienteName}>{ingrediente.Ingrediente}</Text>
            <Text style={styles.ingredienteDetail}>ID: {ingrediente.ID}</Text>
            <Text style={styles.ingredienteDetail}>Unidad de Medida: {ingrediente.Unidad_de_Medida}</Text>
            <Text style={styles.ingredienteDetail}>Tamaño del Paquete: {ingrediente['Tamaño_del_Paquete']}</Text>
            <Text style={styles.ingredienteDetail}>Costo: {ingrediente.Costo}</Text>
          </View>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  ingredientesList: {
    width: '100%',
  },
  ingredienteItem: {
    marginBottom: 10,
    padding: 10,
    backgroundColor: '#f2f2f2',
    borderRadius: 5,
  },
  ingredienteName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  ingredienteDetail: {
    fontSize: 14,
    marginBottom: 2,
  },
});

export default IngredientesScreen;
