import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Text } from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import HomeScreen from '../views/homeScreen';
import IngredientListScreen from '../views/IngredientListScreen';
import TortasScreen from '../views/TortasScreen';

const AppNavigator = () => {
  const [isExpanded, setExpanded] = useState(false);
  const sidebarWidth = isExpanded ? 250 : 60;
  const arrowIcon = isExpanded ? 'chevron-left' : 'chevron-right';

  const toggleMenu = () => {
    setExpanded(!isExpanded);
  };

  const handleDashboardClick = () => {
    // Navegar a la pantalla de inicio (HomeScreen)
  };

  const handleIngredientesClick = () => {
    // Navegar a la pantalla de lista de ingredientes (IngredientListScreen)
  };

  const handleTortasClick = () => {
    // Navegar a la pantalla de tortas (TortasScreen)
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        style={[styles.sidebar, { width: sidebarWidth }]}
        onPress={toggleMenu}
        activeOpacity={0.7}
      >
        <TouchableOpacity style={styles.expandButton} onPress={toggleMenu}>
          <Ionicons name={arrowIcon} size={24} color="white" />
        </TouchableOpacity>
        {isExpanded ? (
          <Text style={styles.logo}>@CostoSmart</Text>
        ) : null}
        {isExpanded && (
          <View style={styles.menuItems}>
            <TouchableOpacity onPress={handleDashboardClick}>
              <Text style={styles.menuItem}>Dashboard</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={handleIngredientesClick}>
              <Text style={styles.menuItem}>Ingredientes</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={handleTortasClick}>
              <Text style={styles.menuItem}>Tortas</Text>
            </TouchableOpacity>
          </View>
        )}
        <View style={styles.separator} />
        <View style={styles.bottomMenu}>
          <TouchableOpacity onPress={() => console.log('Settings clicked')}>
            <Text style={styles.bottomMenuItem}>Settings</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => console.log('Ayuda clicked')}>
            <Text style={styles.bottomMenuItem}>Ayuda</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => console.log('Contactanos clicked')}>
            <Text style={styles.bottomMenuItem}>Contactanos</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => console.log('Cerrar Sesion clicked')}>
            <Text style={styles.bottomMenuItem}>Cerrar Sesi√≥n</Text>
          </TouchableOpacity>
        </View>
      </TouchableOpacity>
      <View style={styles.content}>
        {/* Cambia el texto "Home" por el componente HomeScreen */}
        <HomeScreen />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  sidebar: {
    backgroundColor: '#333',
    paddingTop: 40,
    alignItems: 'center',
    justifyContent: 'flex-start',
    zIndex: 1,
  },
  expandButton: {
    position: 'absolute',
    top: 10,
    left: 10,
    padding: 5,
    borderRadius: 5,
    backgroundColor: '#555',
  },
  logo: {
    fontSize: 20,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 20,
  },
  hiddenText: {
    opacity: 0,
  },
  menuItems: {
    marginTop: 20,
  },
  menuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 10,
  },
  separator: {
    height: 1,
    backgroundColor: 'white',
    marginVertical: 20,
    width: '80%',
  },
  bottomMenu: {
    paddingBottom: 20,
  },
  bottomMenuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 10,
  },
  content: {
    flex: 1,
    backgroundColor: '#fff',
    justifyContent: 'center',
    alignItems: 'center',
  },
  screenTitle: {
    fontSize: 24,
    fontWeight: 'bold',
  },
});

export default AppNavigator;
