import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { Ionicons } from '@expo/vector-icons';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

import HomeScreen from '../views/homeScreen';
import IngredientListScreen from '../views/IngredientListScreen';
import TortasScreen from '../views/TortasScreen';
import NewIngredientScreen from '../views/NewIngredientScreen';

const Stack = createNativeStackNavigator();

const AppNavigator = () => {
  const [isExpanded, setExpanded] = useState(false);
  const navigation = useNavigation();

  const toggleMenu = () => {
    setExpanded(!isExpanded);
  };

  return (
    <View style={styles.container}>
      <View style={[styles.menu, isExpanded ? styles.menuExpanded : null]}>
        <TouchableOpacity onPress={toggleMenu} activeOpacity={0.7}>
          <Ionicons name={isExpanded ? 'close' : 'menu'} size={30} color="black" />
        </TouchableOpacity>
        {isExpanded && (
          <View style={styles.menuItems}>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Home');
              }}
            >
              <Text style={styles.menuItem}>Overview</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Home');
              }}
            >
              <Text style={styles.menuItem}>Summary</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Tortas');
              }}
            >
              <Text style={styles.menuItem}>Tortas</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('IngredientStack');
              }}
            >
              <Text style={styles.menuItem}>Ingredientes</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Home');
              }}
            >
              <Text style={styles.menuItem}>Recetas</Text>
            </TouchableOpacity>
          </View>
        )}
      </View>
      <View style={styles.ingredientsContainer}>
        <Stack.Navigator
          initialRouteName="Home"
          screenOptions={{
            headerStyle: {
              backgroundColor: '#bbe4fc',
            },
            headerTintColor: 'black',
            headerTitleStyle: {
              fontWeight: 'bold',
            },
            headerLeft: () => (
              <TouchableOpacity onPress={toggleMenu} activeOpacity={0.7} style={styles.headerMenu}>
                <Ionicons name={isExpanded ? 'close' : 'menu'} size={30} color="black" />
              </TouchableOpacity>
            ),
          }}
        >
          <Stack.Screen name="Home" component={HomeScreen} />
          <Stack.Screen name="IngredientStack" component={IngredientListScreen} />
          <Stack.Screen name="Tortas" component={TortasScreen} />
          <Stack.Screen name="NewIngredient" component={NewIngredientScreen} />
        </Stack.Navigator>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  menu: {
    width: 60,
    backgroundColor: '#bbe4fc',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 1,
  },
  menuExpanded: {
    width: 100,
  },
  menuItems: {
    marginTop: 10,
  },
  menuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'black',
    marginBottom: 10,
  },
  ingredientsContainer: {
    flex: 1,
  },
  headerMenu: {
    marginLeft: 10,
  },
});

export default AppNavigator;
