// NewIngredientScreen.js
import React, { useState } from 'react';
import { View, Text, TextInput, Button } from 'react-native';
import { agregarIngrediente } from '../controllers/IngredientController';
import styles from '../styles/styles';

const NewIngredientScreen = ({ navigation }) => {
  const [nombre, setNombre] = useState('');
  const [unidadMedida, setUnidadMedida] = useState('');
  const [tamanoPaquete, setTamanoPaquete] = useState('');
  const [costo, setCosto] = useState('');
  const [stock, setStock] = useState('');

  const handleAgregarIngrediente = async () => {
    const ingredientData = {
      nombre,
      unidad_Medida: unidadMedida,
      tamano_Paquete: tamanoPaquete,
      costo,
      CantidadStock: stock,
    };

    try {
      const nuevoIngredienteAgregado = await agregarIngrediente(ingredientData);
      console.log('Ingrediente agregado:', nuevoIngredienteAgregado);
      // Reiniciar los campos del formulario después de agregar el ingrediente
      setNombre('');
      setUnidadMedida('');
      setTamanoPaquete('');
      setCosto('');
      setStock('');
      // Volver a la pantalla anterior
      navigation.goBack();
    } catch (error) {
      console.error('Error al agregar el ingrediente:', error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Nuevo Ingrediente</Text>
      <TextInput
        style={styles.input}
        placeholder="Nombre"
        value={nombre}
        onChangeText={setNombre}
      />
      <TextInput
        style={styles.input}
        placeholder="Unidad de Medida"
        value={unidadMedida}
        onChangeText={setUnidadMedida}
      />
      <TextInput
        style={styles.input}
        placeholder="Tamaño de Paquete"
        value={tamanoPaquete}
        onChangeText={setTamanoPaquete}
      />
      <TextInput
        style={styles.input}
        placeholder="Costo"
        value={costo}
        onChangeText={setCosto}
      />
      <TextInput
        style={styles.input}
        placeholder="Stock"
        value={stock}
        onChangeText={setStock}
      />
      <Button title="Agregar" onPress={handleAgregarIngrediente} />
    </View>
  );
};

export default NewIngredientScreen;
