import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, Button, Picker } from 'react-native';
import axios from 'axios';

const HomeScreen = () => {
  const [listaPrecios, setListaPrecios] = useState([]);
  const [selectedTorta, setSelectedTorta] = useState(null);
  const [selectedTortaNombre, setSelectedTortaNombre] = useState('');

  useEffect(() => {
    obtenerListaPrecios();
  }, []);

  const obtenerListaPrecios = async () => {
    try {
      const response = await axios.get('/lista_precios');
      setListaPrecios(response.data);
    } catch (error) {
      console.error('Error al obtener la lista de precios:', error);
    }
  };

  const renderizarItem = ({ item }) => (
    <View>
      <Text>{item.nombre_torta}</Text>
      <Text>{item.costo_total}</Text>
    </View>
  );

  const renderizarEncabezado = () => (
    <View>
      <Text>Nombre Torta</Text>
      <Text>Costo Total</Text>
    </View>
  );

  const handleConfirmarVenta = async () => {
    if (selectedTorta) {
      try {
        const response = await axios.post('/ventas', {
          id_torta: selectedTorta,
          nombre_torta: selectedTortaNombre,
          costo_torta: listaPrecios.find((torta) => torta.id_torta === selectedTorta).costo_total,
        });

        console.log('Venta registrada:', response.data);
        // Realizar cualquier otra acción necesaria después de registrar la venta
      } catch (error) {
        console.error('Error al registrar la venta:', error);
      }
    }
  };

  return (
    <View>
      <Text>PRECIOS</Text>
      <Picker
        selectedValue={selectedTorta}
        onValueChange={(itemValue, itemIndex) => {
          setSelectedTorta(itemValue);
          setSelectedTortaNombre(listaPrecios[itemIndex].nombre_torta);
        }}
      >
        <Picker.Item label="Seleccionar torta" value={null} />
        {listaPrecios.map((torta) => (
          <Picker.Item key={torta.id_torta} label={torta.nombre_torta} value={torta.id_torta} />
        ))}
      </Picker>
      {renderizarEncabezado()}
      <FlatList data={listaPrecios} renderItem={renderizarItem} keyExtractor={(item) => item.id_torta.toString()} />
      <Button title="Confirmar Venta" onPress={handleConfirmarVenta} disabled={!selectedTorta} />
    </View>
  );
};

export default HomeScreen;
