import React, { useState, useEffect } from 'react';
import { View, FlatList, Text, TouchableOpacity } from 'react-native';
import { fetchIngredientes } from '../controllers/IngredientController';
import styles from '../styles/styles';

const HomeScreen = ({ navigation }) => {
  const [ingredientes, setIngredientes] = useState([]);

  useEffect(() => {
    fetchIngredientesData();
  }, []);

  const fetchIngredientesData = async () => {
    try {
      const data = await fetchIngredientes();
      console.log('Datos de ingredientes:', data); // Agrega este console.log
      setIngredientes(data);
    } catch (error) {
      console.error(error);
    }
  };

  const renderIngrediente = ({ item }) => (
    <View style={styles.row}>
      <Text style={styles.cell}>{item.nombre}</Text>
      <Text style={styles.cell}>{item.unidadMedida}</Text>
      <Text style={styles.cell}>{item.tamanoPaquete}</Text>
      <Text style={styles.cell}>{item.costo}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Lista de Ingredientes</Text>
      <FlatList
        data={ingredientes}
        renderItem={renderIngrediente}
        keyExtractor={(item) => item.id.toString()}
        style={styles.list}
      />
      <TouchableOpacity
        style={styles.addButton}
        onPress={() => navigation.navigate('IngredientForm')}
      >
        <Text style={styles.addButtonText}>Agregar Ingrediente</Text>
      </TouchableOpacity>
    </View>
  );
};

export default HomeScreen;
