import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { createDrawerNavigator } from '@react-navigation/drawer';

import HomeScreen from '../views/homeScreen';
import IngredientListScreen from '../views/IngredientListScreen';
import TortasScreen from '../views/TortasScreen';
import NewIngredientScreen from '../views/NewIngredientScreen';

const Drawer = createDrawerNavigator();

const AppNavigator = () => {
  const [isExpanded, setExpanded] = useState(false);
  const navigation = useNavigation();
  const sidebarWidth = isExpanded ? 250 : 60;

  const toggleMenu = () => {
    setExpanded(!isExpanded);
  };

  return (
    <View style={styles.container}>
      <View style={[styles.sidebar, { width: sidebarWidth }]}>
        <TouchableOpacity style={styles.menu} onPress={toggleMenu} activeOpacity={0.7}>
          <Text style={styles.menuText}>{isExpanded ? 'Cerrar' : 'Menú'}</Text>
        </TouchableOpacity>
        {isExpanded && (
          <View style={styles.menuItems}>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('IngredientStack');
              }}
            >
              <Text style={styles.menuItem}>Ingredientes</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Tortas');
              }}
            >
              <Text style={styles.menuItem}>Tortas</Text>
            </TouchableOpacity>
          </View>
        )}
      </View>
      <View style={styles.content}>
        <Drawer.Navigator
          initialRouteName="Home"
          drawerStyle={styles.drawerStyle}
          drawerContentOptions={{
            labelStyle: styles.drawerLabel,
          }}
          drawerType={isExpanded ? 'slide' : 'front'}
          overlayColor="transparent"
        >
          <Drawer.Screen name="Home" component={HomeScreen} options={{ headerShown: false }} />
          <Drawer.Screen name="IngredientStack" component={IngredientListScreen} options={{ headerShown: false }} />
          <Drawer.Screen name="Tortas" component={TortasScreen} options={{ headerShown: false }} />
          <Drawer.Screen name="NewIngredient" component={NewIngredientScreen} options={{ headerShown: false }} />
        </Drawer.Navigator>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  sidebar: {
    backgroundColor: '#bbe4fc',
    alignItems: 'center',
    justifyContent: 'flex-start',
    zIndex: 1,
  },
  menu: {
    width: '100%',
    height: 60,
    alignItems: 'center',
    justifyContent: 'center',
  },
  menuText: {
    fontSize: 18,
    fontWeight: 'bold',
    color: 'black',
  },
  menuItems: {
    marginTop: 10,
  },
  menuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'black',
    marginBottom: 10,
  },
  content: {
    flex: 1,
  },
  drawerStyle: {
    backgroundColor: '#bbe4fc',
    width: 250,
  },
  drawerLabel: {
    fontFamily: 'Source Sans 3',
  },
});

export default AppNavigator;
