import React, { useState } from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { Ionicons } from '@expo/vector-icons';
import { View, TouchableOpacity, StyleSheet, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';

import HomeScreen from '../views/homeScreen';
import IngredientListScreen from '../views/IngredientListScreen';
import TortasScreen from '../views/TortasScreen';

const Tab = createBottomTabNavigator();

const TabNavigator = () => {
  const [isExpanded, setExpanded] = useState(false);
  const navigation = useNavigation();

  const toggleMenu = () => {
    setExpanded(!isExpanded);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        style={[styles.menu, isExpanded ? styles.menuExpanded : null]}
        onPress={toggleMenu}
        activeOpacity={0.7}
      >
        <Ionicons name={isExpanded ? 'close' : 'menu'} size={30} color="black" />
        {isExpanded && (
          <View style={styles.menuItems}>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Home');
              }}
            >
              <Text style={styles.menuItem}>Overview</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Home');
              }}
            >
              <Text style={styles.menuItem}>Summary</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Tortas');
              }}
            >
              <Text style={styles.menuItem}>Tortas</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('IngredientStack');
              }}
            >
              <Text style={styles.menuItem}>Ingredientes</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Home');
              }}
            >
              <Text style={styles.menuItem}>Recetas</Text>
            </TouchableOpacity>
          </View>
        )}
      </TouchableOpacity>
      <Tab.Navigator
        tabBarOptions={{
          activeTintColor: '#86C997',
          inactiveTintColor: 'gray',
          labelStyle: {
            fontSize: 12,
            fontWeight: 'bold',
          },
          style: {
            backgroundColor: '#86C997',
            borderTopWidth: 1,
            borderTopColor: '#ccc',
          },
          showIcon: true,
          showLabel: true,
          tabStyle: {
            flexDirection: 'row',
          },
          indicatorStyle: {
            backgroundColor: '#86C997',
          },
        }}
        initialRouteName="Home"
      >
        
        
        <Tab.Screen
          name="Tortas"
          component={TortasScreen}
          options={{
            tabBarLabel: 'Tortas',
            tabBarIcon: ({ color, size }) => (
              <Ionicons name="cake" size={size} color={color} />
            ),
          }}
        />
      </Tab.Navigator>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  menu: {
    width: 60,
    backgroundColor: '#86C997',
    alignItems: 'center',
    justifyContent: 'center',
    transitionProperty: 'width',
    transitionDuration: '0.3s',
    zIndex: 1,
  },
  menuExpanded: {
    width: 120,
  },
  menuItems: {
    marginTop: 10,
  },
  menuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'black',
    marginBottom: 10,
  },
});

export default TabNavigator;
