import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity } from 'react-native';
import styles from '../styles/styles';

const NewIngredientScreen = ({ handleAgregarIngrediente, closeModal }) => {
  const [nombre, setNombre] = useState('');
  const [unidadMedida, setUnidadMedida] = useState('');
  const [tamanoPaquete, setTamanoPaquete] = useState('');
  const [costo, setCosto] = useState('');
  const [stock, setStock] = useState('');

  const handleAgregar = () => {
    const ingredientData = {
      nombre,
      unidad_Medida: unidadMedida,
      tamano_Paquete: tamanoPaquete,
      costo,
      CantidadStock: stock,
    };

    handleAgregarIngrediente(ingredientData);
  };

  return (
    <View style={styles.responsiveContainer}>
      <View style={styles.responsiveFormContainer}>
        <Text style={styles.formTitle}>Nuevo Ingrediente</Text>
        <TextInput
          style={styles.input}
          placeholder="Nombre"
          value={nombre}
          onChangeText={setNombre}
        />
        <TextInput
          style={styles.input}
          placeholder="Unidad de Medida"
          value={unidadMedida}
          onChangeText={setUnidadMedida}
        />
        <TextInput
          style={styles.input}
          placeholder="TamaÃ±o de Paquete"
          value={tamanoPaquete}
          onChangeText={setTamanoPaquete}
        />
        <TextInput
          style={styles.input}
          placeholder="Costo"
          value={costo}
          onChangeText={setCosto}
        />
        <TextInput
          style={styles.input}
          placeholder="Stock"
          value={stock}
          onChangeText={setStock}
        />
        <View style={styles.buttonContainer}>
          <TouchableOpacity style={styles.addButton} onPress={handleAgregar}>
            <Text style={styles.buttonText}>Agregar</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.cancelButton} onPress={closeModal}>
            <Text style={styles.buttonText}>Cancelar</Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
};

export default NewIngredientScreen;
