import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Text } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { Ionicons } from '@expo/vector-icons';
import { createDrawerNavigator } from '@react-navigation/drawer';

import HomeScreen from '../views/homeScreen';
import IngredientListScreen from '../views/IngredientListScreen';
import TortasScreen from '../views/TortasScreen';
import NewIngredientScreen from '../views/NewIngredientScreen';

const Drawer = createDrawerNavigator();

const AppNavigator = () => {
  const [isExpanded, setExpanded] = useState(false);
  const navigation = useNavigation();

  const toggleMenu = () => {
    setExpanded(!isExpanded);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        style={[styles.menu, isExpanded ? styles.menuExpanded : null]}
        onPress={toggleMenu}
        activeOpacity={0.7}
      >
        <Ionicons name={isExpanded ? 'close' : 'menu'} size={30} color="black" />
        {isExpanded && (
          <View style={styles.menuItems}>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Home');
              }}
            >
              <Text style={styles.menuItem}>Overview</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Home');
              }}
            >
              <Text style={styles.menuItem}>Summary</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Tortas');
              }}
            >
              <Text style={styles.menuItem}>Tortas</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('IngredientStack');
              }}
            >
              <Text style={styles.menuItem}>Ingredientes</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Home');
              }}
            >
              <Text style={styles.menuItem}>Recetas</Text>
            </TouchableOpacity>
          </View>
        )}
      </TouchableOpacity>
      <Drawer.Navigator
        initialRouteName="Home"
        drawerStyle={styles.drawerStyle}
        drawerContentOptions={{
          labelStyle: styles.drawerLabel,
        }}
      >
        
      </Drawer.Navigator>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  menu: {
    width: 80,
    backgroundColor: '#bbe4fc',
    alignItems: 'center',
    justifyContent: 'center',
    transitionProperty: 'width',
    transitionDuration: '0.3s',
    zIndex: 1,
  },
  menuExpanded: {
    width: 120,
  },
  menuItems: {
    marginTop: 10,
  },
  menuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'black',
    marginBottom: 10,
  },
  drawerStyle: {
    backgroundColor: '#bbe4fc',
    width: 250,
  },
  drawerLabel: {
    fontFamily: 'Source Sans 3',
  },
});

export default AppNavigator;
