import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';

const abmIngredientesScreen = () => {
  const [ingrediente, setIngrediente] = useState('');
  const [id, setId] = useState('');
  const [unidadMedida, setUnidadMedida] = useState('');
  const [tamanoPaquete, setTamanoPaquete] = useState('');
  const [costo, setCosto] = useState('');

  const handleGuardar = () => {
    // L칩gica para guardar el ingrediente en la base de datos
    console.log('Guardando ingrediente...');
    console.log('Ingrediente:', ingrediente);
    console.log('ID:', id);
    console.log('Unidad de Medida:', unidadMedida);
    console.log('Tama침o del Paquete:', tamanoPaquete);
    console.log('Costo:', costo);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>ABM de Ingredientes</Text>
      <View style={styles.form}>
        <Text style={styles.label}>Ingrediente:</Text>
        <TextInput
          style={styles.input}
          value={ingrediente}
          onChangeText={text => setIngrediente(text)}
        />
        <Text style={styles.label}>ID:</Text>
        <TextInput
          style={styles.input}
          value={id}
          onChangeText={text => setId(text)}
        />
        <Text style={styles.label}>Unidad de Medida:</Text>
        <TextInput
          style={styles.input}
          value={unidadMedida}
          onChangeText={text => setUnidadMedida(text)}
        />
        <Text style={styles.label}>Tama침o del Paquete:</Text>
        <TextInput
          style={styles.input}
          value={tamanoPaquete}
          onChangeText={text => setTamanoPaquete(text)}
        />
        <Text style={styles.label}>Costo:</Text>
        <TextInput
          style={styles.input}
          value={costo}
          onChangeText={text => setCosto(text)}
        />
        <Button title="Guardar" onPress={handleGuardar} />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  form: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 4,
    padding: 10,
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 4,
    marginBottom: 10,
    padding: 5,
  },
});

export default abmIngredientesScreen;
