import React from 'react';
import { createAppContainer } from 'react-navigation';
import { createMaterialTopTabNavigator } from 'react-navigation-tabs';
import { Entypo } from '@expo/vector-icons';
import HomeScreen from '../views/homeScreen';
import IngredientListScreen from '../views/IngredientListScreen';
import TortasScreen from '../views/TortasScreen';
import styles from '../styles/styles';

const TabNavigator = createMaterialTopTabNavigator(
  {
    IngredientList: {
      screen: IngredientListScreen,
      navigationOptions: {
        tabBarLabel: 'Ingredientes',
        tabBarIcon: ({ tintColor }) => <Entypo name="list" size={24} color={tintColor} />,
      },
    },
    Home: {
      screen: HomeScreen,
      navigationOptions: {
        tabBarLabel: 'Inicio',
        tabBarIcon: ({ tintColor }) => <Entypo name="home" size={24} color={tintColor} />,
      },
    },
    Tortas: {
      screen: TortasScreen,
      navigationOptions: {
        tabBarLabel: 'Tortas',
        tabBarIcon: ({ tintColor }) => <Entypo name="home" size={24} color={tintColor} />,
      },
    },
  },
  {
    tabBarOptions: {
      activeTintColor: '#86C997',
      inactiveTintColor: 'gray',
      labelStyle: {
        fontSize: 12,
        fontWeight: 'bold',
      },
      style: {
        backgroundColor: '#86C997', // Cambiar el color de fondo a verde
        borderTopWidth: 1,
        borderTopColor: '#ccc',
      },
      showIcon: true, // Mostrar los iconos en la barra de navegación
      showLabel: true, // Mostrar las etiquetas en la barra de navegación
      tabStyle: {
        flexDirection: 'row',
      },
      indicatorStyle: {
        backgroundColor: '#86C997',
      },
    },
    tabBarPosition: 'top', // Asegurar que la barra de navegación esté en la parte superior
    swipeEnabled: true, // Habilitar el deslizamiento entre pestañas
    initialRouteName: 'Home', // Establecer la pestaña inicial como "Inicio"
    order: ['IngredientList', 'Home', 'Tortas'], // Ordenar las pestañas como "Ingredientes - Inicio - Tortas"
    defaultNavigationOptions: {
      tabBarLabelStyle: styles.menuText, // Aplicar el estilo de los textos del menú a todas las etiquetas
    },
  }
);

export default createAppContainer(TabNavigator);
