import React, { useState, useEffect } from 'react';

const TortasScreen = () => {
  const [tortas, setTortas] = useState([]);

  useEffect(() => {
    fetchTortas();
  }, []);

  const fetchTortas = async () => {
    try {
      const response = await fetch('http://localhost:3000/tortas');
      const data = await response.json();

      // Modificar los nombres de los campos en el JSON
      const tortasData = data.map((torta) => ({
        id: torta.ID_TORTA,
        nombre: torta.NOMBRE_TORTA,
        cantidad: torta.CANTIDAD_INGREDIENTE,
        unidadMedida: torta.UNIDAD_MEDIDA,
      }));

      setTortas(tortasData);
    } catch (error) {
      console.error('Error al obtener las tortas:', error);
    }
  };

  return (
    <div>
      <h1>Tortas</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Cantidad</th>
            <th>Unidad de Medida</th>
          </tr>
        </thead>
        <tbody>
          {tortas.map((torta) => (
            <tr key={torta.id}>
              <td>{torta.id}</td>
              <td>{torta.nombre}</td>
              <td>{torta.cantidad}</td>
              <td>{torta.unidadMedida}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TortasScreen;
