// --- src/screens/HomeScreen.js ---

import React, { useState, useEffect } from 'react';
import { View, FlatList, Text } from 'react-native';
import styles from '../styles';

const HomeScreen = ({ navigation }) => {
  const [listaPrecios, setListaPrecios] = useState([]);

  useEffect(() => {
    fetchListaPrecios();
  }, []);

  const fetchListaPrecios = async () => {
    try {
      const response = await fetch('http://localhost:3000/lista_precios');
      const data = await response.json();
      setListaPrecios(data);
    } catch (error) {
      console.error(error);
    }
  };

  const renderPreciosItem = ({ item }) => (
    <View style={styles.row}>
      <Text style={styles.cell}>{item.nombre_torta}</Text>
      <Text style={styles.cell}>{item.costo_total}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={listaPrecios}
        renderItem={renderPreciosItem}
        keyExtractor={(item) => item.id_torta.toString()}
        style={styles.table}
      />
    </View>
  );
};

export default HomeScreen;
