import React, { useState, useEffect } from 'react';
import { View, FlatList, Text, TouchableOpacity, Modal, TextInput, Button } from 'react-native';
import styles from '../styles/styles';

// Define un componente de tarjeta para mostrar cada receta
const RecetaCard = ({ receta, onEdit, onDelete }) => {
  return (
    <TouchableOpacity
      style={styles.card}
      onPress={() => onEdit(receta.idTorta)}
      onLongPress={() => onDelete(receta.idTorta)}
    >
      <Text style={styles.cardTitle}>ID Torta: {receta.idTorta}</Text>
      {receta.ingredientes.map((ingrediente, index) => (
        <View key={index}>
          <Text style={styles.cardSubtitle}>ID Ingrediente: {ingrediente.idIngrediente}</Text>
          <Text style={styles.cardValue}>Cantidad Ingrediente: {ingrediente.cantidad}</Text>
        </View>
      ))}
    </TouchableOpacity>
  );
};

