    />
  );

  // Resto de tu código para la función de eliminar receta y el modal de edición

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Lista de Recetas</Text>
      <FlatList
        data={recetas}
        renderItem={renderizarItem}
        keyExtractor={(item) => item.idTorta} // Usa el idTorta como clave
      />
