import React, { useState, useEffect } from 'react';
import { View, Text, Button, Picker } from 'react-native';
import axios from 'axios';

const HomeScreen = () => {
  const [tortas, setTortas] = useState([]);
  const [selectedTorta, setSelectedTorta] = useState(null);

  useEffect(() => {
    obtenerTortas();
  }, []);

  const obtenerTortas = () => {
    axios.get('/tortas')
      .then((response) => {
        setTortas(response.data);
      })
      .catch((error) => {
        console.error('Error al obtener las tortas:', error);
      });
  };

  const handleConfirmarVenta = () => {
    if (selectedTorta) {
      // Aqu√≠ puedes enviar la solicitud al backend para registrar la venta con la torta seleccionada
      console.log('Torta seleccionada:', selectedTorta);
    }
  };

  return (
    <View>
      <Text>Home Screen</Text>
      <Button title="Nueva Venta" onPress={() => {}}>
        <Picker
          selectedValue={selectedTorta}
          onValueChange={(itemValue) => setSelectedTorta(itemValue)}
        >
          <Picker.Item label="Seleccionar torta" value={null} />
          {tortas.map((torta) => (
            <Picker.Item key={torta.id} label={torta.nombre_torta} value={torta.id} />
          ))}
        </Picker>
      </Button>
      <Button title="Confirmar Venta" onPress={handleConfirmarVenta} />
    </View>
  );
};

export default HomeScreen;
