const TabNavigator = createMaterialTopTabNavigator(
  {
    Tortas: {
      screen: TortasScreen,
      navigationOptions: {
        tabBarLabel: 'Tortas',
        tabBarIcon: ({ tintColor }) => <Entypo name="cake" size={24} color={tintColor} />,
      },
    },
    Home: {
      screen: HomeScreen,
      navigationOptions: {
        tabBarLabel: 'Inicio',
        tabBarIcon: ({ tintColor }) => <Entypo name="home" size={24} color={tintColor} />,
      },
    },
    IngredientList: {
      screen: IngredientListScreen,
      navigationOptions: {
        tabBarLabel: 'Ingredientes',
        tabBarIcon: ({ tintColor }) => <Entypo name="list" size={24} color={tintColor} />,
      },
    },
  },
  {
    tabBarPosition: 'bottom', // Cambiado a bottom para tener la barra de navegación en la parte inferior
    tabBarOptions: {
      activeTintColor: '#86C997',
      inactiveTintColor: 'gray',
      labelStyle: {
        fontSize: 12,
        fontWeight: 'bold',
      },
      style: styles.tabBar, // Usando el estilo definido en styles.js para la barra de navegación
      indicatorStyle: {
        backgroundColor: '#86C997',
      },
    },
  }
);
