import axios from 'axios';

const baseUrl = 'http://localhost:3000';

export const fetchTortas = async () => {
  try {
    const response = await axios.get(`${baseUrl}/tortas`);
    return response.data;
  } catch (error) {
    console.error('Error al obtener las tortas:', error);
    return [];
  }
};

export const agregarTorta = async (torta) => {
  try {
    const response = await axios.post(`${baseUrl}/tortas`, torta);
    return response.data;
  } catch (error) {
    console.error('Error al agregar la torta:', error);
    return { success: false };
  }
};

export const editarTorta = async (torta) => {
  try {
    const response = await axios.put(`${baseUrl}/tortas/${torta.id}`, torta);
    return response.data;
  } catch (error) {
    console.error('Error al editar la torta:', error);
    return { success: false };
  }
};

export const borrarTorta = async (id) => {
  try {
    const response = await axios.delete(`${baseUrl}/tortas/${id}`);
    if (response.status === 204) {
      console.log('Torta eliminada exitosamente');
      return { success: true };
    } else {
      console.error('Error al eliminar la torta');
      return { success: false, error: 'Error al eliminar la torta' };
    }
  } catch (error) {
    console.error('Error en la solicitud de borrado de la torta:', error);
    return { success: false, error };
  }
};
