import React, { useState } from 'react';
import { View, Text } from 'react-native';
import { CssBaseline, Drawer, IconButton, List, ListItem, ListItemIcon, ListItemText, Typography } from '@mui/material';
import { Home as HomeIcon, ShoppingCart as ShoppingCartIcon, People as PeopleIcon, BarChart as BarChartIcon, Layers as LayersIcon, Assignment as AssignmentIcon, Menu as MenuIcon, ChevronLeft as ChevronLeftIcon } from '@mui/icons-material';
import { useNavigation } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import HomeScreen from '../views/homeScreen';
import IngredientListScreen from '../views/IngredientListScreen';
import TortasScreen from '../views/TortasScreen';
import RecetaScreen from '../views/RecetaScreen';

const Stack = createStackNavigator();

const Navigation = () => {
  const [isExpanded, setExpanded] = useState(false);
  const navigation = useNavigation();

  const toggleMenu = () => {
    setExpanded(!isExpanded);
  };

  const handleNavigationClick = (screenName) => {
    navigation.navigate(screenName);
  };

  return (
    <View style={{ flexDirection: 'row' }}>
      <CssBaseline />
      <Drawer variant="permanent" sx={{ width: isExpanded ? 250 : 60, flexShrink: 0 }}>
        <IconButton onClick={toggleMenu}>
          {isExpanded ? <ChevronLeftIcon /> : <MenuIcon />}
        </IconButton>
        <Typography variant="h6" noWrap sx={{ color: 'white', ml: 2, display: isExpanded ? 'block' : 'none' }}>
          @CostoSmart
        </Typography>
        <List>
          <ListItem button onClick={() => handleNavigationClick('Home')}>
            <ListItemIcon>
              <HomeIcon />
            </ListItemIcon>
            <ListItemText primary="Dashboard" />
          </ListItem>
          <ListItem button onClick={() => handleNavigationClick('IngredientListScreen')}>
            <ListItemIcon>
              <ShoppingCartIcon />
            </ListItemIcon>
            <ListItemText primary="Ingredientes" />
          </ListItem>
          <ListItem button onClick={() => handleNavigationClick('TortasScreen')}>
            <ListItemIcon>
              <PeopleIcon />
            </ListItemIcon>
            <ListItemText primary="Tortas" />
          </ListItem>
          <ListItem button onClick={() => handleNavigationClick('RecetaScreen')}>
            <ListItemIcon>
              <BarChartIcon />
            </ListItemIcon>
            <ListItemText primary="Recetas" />
          </ListItem>
        </List>
      </Drawer>
      <View style={{ flex: 1 }}>
        <Stack.Navigator>
          <Stack.Screen
            name="Home"
            component={HomeScreen}
            options={{
              headerShown: false,
            }}
          />
          <Stack.Screen
            name="IngredientListScreen"
            component={IngredientListScreen}
            options={{
              title: 'Ingredientes',
            }}
          />
          <Stack.Screen
            name="TortasScreen"
            component={TortasScreen}
            options={{
              title: 'Tortas',
            }}
          />
          <Stack.Screen
            name="RecetaScreen"
            component={RecetaScreen}
            options={{
              title: 'Receta',
            }}
          />
        </Stack.Navigator>
      </View>
    </View>
  );
};

export default Navigation;
