import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Text, Animated } from 'react-native';

const AppNavigator = () => {
  const [isExpanded, setExpanded] = useState(false);
  const sidebarWidth = isExpanded ? 250 : 60;
  const arrowIcon = isExpanded ? 'right' : 'left';

  const toggleMenu = () => {
    setExpanded(!isExpanded);
  };

  const sidebarWidthAnimation = new Animated.Value(sidebarWidth);

  const animateSidebar = () => {
    Animated.timing(sidebarWidthAnimation, {
      toValue: isExpanded ? 250 : 60,
      duration: 300,
      useNativeDriver: false,
    }).start();
  };

  return (
    <View style={styles.container}>
      <Animated.View style={[styles.sidebar, { width: sidebarWidthAnimation }]}>
        <Text style={styles.logo}>@CostoSmart</Text>
        {isExpanded && (
          <View style={styles.menuItems}>
            <TouchableOpacity onPress={toggleMenu}>
              <Text style={styles.menuItem}>Overview</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={toggleMenu}>
              <Text style={styles.menuItem}>Dashboard</Text>
            </TouchableOpacity>
          </View>
        )}
        <TouchableOpacity style={styles.arrowButton} onPress={toggleMenu}>
          <Text style={styles.arrowIcon}>{arrowIcon}</Text>
        </TouchableOpacity>
      </Animated.View>
      <View style={styles.content}>
        {/* Contenido de la pantalla */}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  sidebar: {
    backgroundColor: '#333',
    paddingTop: 40,
    alignItems: 'center',
    justifyContent: 'flex-start',
    zIndex: 1,
  },
  logo: {
    fontSize: 20,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 20,
  },
  menuItems: {
    marginTop: 20,
  },
  menuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  arrowButton: {
    position: 'absolute',
    bottom: 20,
    right: 10,
  },
  arrowIcon: {
    fontSize: 14,
    color: 'white',
    transform: [{ rotateY: '180deg' }],
  },
  content: {
    flex: 1,
    backgroundColor: '#fff',
  },
});

export default AppNavigator;
