import React, { useState, useEffect } from 'react';
import { View, FlatList, Text, TouchableOpacity, Modal, TextInput, Button } from 'react-native';
import styles from '../styles/styles';
import { fetchRecetas, agregarReceta, editarReceta, borrarReceta } from '../controllers/RecetaController';

const RecetaScreen = () => {
  const [recetas, setRecetas] = useState([]);
  const [modalVisible, setModalVisible] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editRecetaId, setEditRecetaId] = useState('');
  const [idTorta, setIdTorta] = useState('');
  const [idIngrediente, setIdIngrediente] = useState('');
  const [cantidadIngrediente, setCantidadIngrediente] = useState('');
  const [showDeleteButton, setShowDeleteButton] = useState(false);

  useEffect(() => {
    obtenerRecetas();
  }, []);

  const obtenerRecetas = async () => {
    try {
      const data = await fetchRecetas();
      setRecetas(data);
    } catch (error) {
      console.error('Error al obtener las recetas:', error);
    }
  };

  const handleAgregarReceta = async () => {
    const recetaData = {
      id_torta: idTorta,
      id_ingrediente: idIngrediente,
      cantidad: cantidadIngrediente,
    };

    await agregarReceta(recetaData);
    obtenerRecetas();
    closeModal();
  };

  const handleEditarReceta = async () => {
    const recetaData = {
      id: editRecetaId,
      id_torta: idTorta,
      id_ingrediente: idIngrediente,
      cantidad: cantidadIngrediente,
    };

    await editarReceta(recetaData);
    obtenerRecetas();
    closeModal();
  };

  const handleEliminarReceta = async (recetaId) => {
    await borrarReceta(recetaId);
    obtenerRecetas();
  };

  const handleOpenModal = (recetaId) => {
    const selectedReceta = recetas.find((receta) => receta.id === recetaId);
    if (selectedReceta) {
      setEditMode(true);
      setEditRecetaId(selectedReceta.id);
      setIdTorta(selectedReceta.id_torta);
      setIdIngrediente(selectedReceta.id_ingrediente);
      setCantidadIngrediente(selectedReceta.cantidad);
      setShowDeleteButton(true);
      setModalVisible(true);
    }
  };

  const closeModal = () => {
    setEditMode(false);
    setEditRecetaId('');
    setIdTorta('');
    setIdIngrediente('');
    setCantidadIngrediente('');
    setShowDeleteButton(false);
    setModalVisible(false);
  };

  const renderizarItem = ({ item }) => (
    <TouchableOpacity
      style={styles.row}
      onPress={() => handleOpenModal(item.id)}
      onLongPress={() => handleEliminarReceta(item.id)}
    >
      <Text style={styles.cell}>ID Torta: {item.id_torta}</Text>
      <Text style={styles.cell}>ID Ingrediente: {item.id_ingrediente}</Text>
      <Text style={styles.cell}>Cantidad Ingrediente: {item.cantidad}</Text>
    </TouchableOpacity>
  );

  const renderizarEncabezado = () => (
    <View style={styles.row}>
      <Text style={[styles.cell, styles.encabezado]}>ID Torta</Text>
      <Text style={[styles.cell, styles.encabezado]}>ID Ingrediente</Text>
      <Text style={[styles.cell, styles.encabezado]}>Cantidad Ingrediente</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Lista de Recetas</Text>
      {renderizarEncabezado()}
      <FlatList
        data={recetas}
        renderItem={renderizarItem}
        keyExtractor={(item) => item?.id?.toString()}
      />
      <TouchableOpacity style={styles.addButton} onPress={() => setModalVisible(true)}>
        <Text style={styles.addButtonText}>Agregar</Text>
      </TouchableOpacity>

      <Modal visible={modalVisible} animationType="slide">
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>{editMode ? 'Editar Receta' : 'Agregar Receta'}</Text>
            <TextInput
              style={styles.input}
              placeholder="ID Torta"
              value={idTorta}
              onChangeText={(text) => setIdTorta(text)}
            />
            <TextInput
              style={styles.input}
              placeholder="ID Ingrediente"
              value={idIngrediente}
              onChangeText={(text) => setIdIngrediente(text)}
            />
            <TextInput
              style={styles.input}
              placeholder="Cantidad Ingrediente"
              value={cantidadIngrediente}
              onChangeText={(text) => setCantidadIngrediente(text)}
            />
            <View style={styles.modalButtonContainer}>
              {showDeleteButton && (
                <TouchableOpacity style={styles.deleteButton} onPress={() => handleEliminarReceta(editRecetaId)}>
                  <Text style={styles.deleteButtonText}>Eliminar</Text>
                </TouchableOpacity>
              )}
              <Button
                title={editMode ? 'Editar' : 'Agregar'}
                onPress={editMode ? handleEditarReceta : handleAgregarReceta}
              />
              <Button title="Cancelar" onPress={closeModal} color="#ccc" />
            </View>
          </View>
        </View>
      </Modal>
    </View>
  );
};

export default RecetaScreen;
