import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Text, Animated } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { AntDesign } from '@expo/vector-icons';

import HomeScreen from '../views/homeScreen';
import IngredientListScreen from '../views/IngredientListScreen';
import TortasScreen from '../views/TortasScreen';


const Drawer = createDrawerNavigator();

const AppNavigator = () => {
  const [isExpanded, setExpanded] = useState(false);
  const navigation = useNavigation();
  const sidebarWidth = isExpanded ? 250 : 60;
  const arrowIcon = isExpanded ? 'left' : 'right';

  const toggleMenu = () => {
    setExpanded(!isExpanded);
  };

  const sidebarWidthAnimation = new Animated.Value(sidebarWidth);

  const animateSidebar = () => {
    Animated.timing(sidebarWidthAnimation, {
      toValue: isExpanded ? 250 : 60,
      duration: 300,
      useNativeDriver: false,
    }).start();
  };

  return (
    <View style={styles.container}>
      <Animated.View style={[styles.sidebar, { width: sidebarWidthAnimation }]}>
        <TouchableOpacity style={styles.menu} onPress={toggleMenu} activeOpacity={0.7}>
          <AntDesign name={arrowIcon} size={18} color="black" />
        </TouchableOpacity>
        {isExpanded && (
          <View style={styles.menuItems}>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Home');
              }}
            >
              <Text style={styles.menuItem}>Home</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('IngredientStack');
              }}
            >
              <Text style={styles.menuItem}>Listado de Ingredientes</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Tortas');
              }}
            >
              <Text style={styles.menuItem}>Listado de Tortas</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('RecipeList');
              }}
            >
              <Text style={styles.menuItem}>Listado de Recetas</Text>
            </TouchableOpacity>
          </View>
        )}
      </Animated.View>
      <View style={styles.content}>
        <Drawer.Navigator
          initialRouteName="Home"
          drawerStyle={styles.drawerStyle}
          drawerContentOptions={{
            labelStyle: styles.drawerLabel,
          }}
          drawerType={isExpanded ? 'slide' : 'front'}
          overlayColor="transparent"
        >
          <Drawer.Screen name="Home" component={HomeScreen} options={{ headerShown: false }} />
          <Drawer.Screen name="IngredientStack" component={IngredientListScreen} options={{ headerShown: false }} />
          <Drawer.Screen name="Tortas" component={TortasScreen} options={{ headerShown: false }} />
  
        </Drawer.Navigator>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  sidebar: {
    backgroundColor: '#bbe4fc',
    alignItems: 'center',
    justifyContent: 'flex-start',
    zIndex: 1,
    paddingHorizontal: 5,
  },
  menu: {
    width: 30,
    height: 30,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 10,
  },
  menuItems: {
    marginTop: 10,
  },
  menuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'black',
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  content: {
    flex: 1,
  },
  drawerStyle: {
    backgroundColor: '#bbe4fc',
    width: '100%',
  },
  drawerLabel: {
    fontFamily: 'Source Sans 3',
  },
});

export default AppNavigator;
