import React, { useState, useEffect } from 'react';
import { View, FlatList, Text } from 'react-native';
import { fetchTortas } from '../controllers/TortaController';
import styles from '../styles';

const TortasScreen = () => {
  const [tortas, setTortas] = useState([]);

  useEffect(() => {
    fetchTortasData();
  }, []);

  const fetchTortasData = async () => {
    try {
      const data = await fetchTortas();
      setTortas(data);
    } catch (error) {
      console.error(error);
    }
  };

  const renderTorta = ({ item }) => (
    <View style={styles.row}>
      <Text style={styles.cell}>{item.nombreTorta}</Text>
      <Text style={styles.cell}>{item.cantidadIngrediente}</Text>
      <Text style={styles.cell}>{item.unidadMedida}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Lista de Tortas</Text>
      <FlatList
        data={tortas}
        renderItem={renderTorta}
        keyExtractor={(item) => item.idTorta.toString()}
        style={styles.list}
      />
    </View>
  );
};

export default TortasScreen;
