import React, { useState, useEffect } from 'react';
import { View, FlatList, Text, TouchableOpacity, Modal, TextInput, Button } from 'react-native';
import styles from '../styles/styles';
import { fetchTortas, agregarTorta, editarTorta, borrarTorta } from '../controllers/TortaController';

const TortasScreen = () => {
  const [tortas, setTortas] = useState([]);
  const [modalVisible, setModalVisible] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editTortaId, setEditTortaId] = useState('');
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [showDeleteButton, setShowDeleteButton] = useState(false);

  useEffect(() => {
    obtenerTortas();
  }, []);

  const obtenerTortas = async () => {
    try {
      const data = await fetchTortas();
      setTortas(data);
    } catch (error) {
      console.error('Error al obtener las tortas:', error);
    }
  };

  const handleAgregarTorta = async () => {
    const tortaData = {
      nombre: nombre,
      descripcion: descripcion,
    };

    await agregarTorta(tortaData);
    obtenerTortas();
    closeModal();
  };

  const handleEditarTorta = async () => {
    const tortaData = {
      id: editTortaId,
      nombre: nombre,
      descripcion: descripcion,
    };
  
    try {
      const response = await editarTorta(tortaData);
  
      if (response.success) {
        // Si la edición fue exitosa, actualiza la lista de tortas
        obtenerTortas();
        closeModal();
      } else {
        // Maneja el caso en el que la edición no sea exitosa, puedes mostrar un mensaje de error o tomar otra acción aquí
        console.error('Error al editar la torta:', response.error);
      }
    } catch (error) {
      // Maneja cualquier error de red o solicitud aquí
      console.error('Error al editar la torta:', error);
    }
  };
  const handleEliminarTorta = async (tortaId) => {
    await borrarTorta(tortaId);
    obtenerTortas();
  };

  const handleOpenModal = (tortaId) => {
    const selectedTorta = tortas.find((torta) => torta.ID_TORTA === tortaId);
    if (selectedTorta) {
      setEditMode(true);
      setEditTortaId(selectedTorta.ID_TORTA);
      setNombre(selectedTorta.nombre_torta);
      setDescripcion(selectedTorta.descripcion_torta);
      setShowDeleteButton(true);
      setModalVisible(true);
    }
  };

  const closeModal = () => {
    setEditMode(false);
    setEditTortaId('');
    setNombre('');
    setDescripcion('');
    setShowDeleteButton(false);
    setModalVisible(false);
  };

  const renderizarItem = ({ item }) => (
    <TouchableOpacity
      style={styles.row}
      onPress={() => handleOpenModal(item.ID_TORTA)}
      onLongPress={() => handleEliminarTorta(item.ID_TORTA)}
    >
      <Text style={styles.cell}>{item.nombre_torta}</Text>
      <Text style={styles.cell}>{item.descripcion_torta}</Text>
    </TouchableOpacity>
  );

  const renderizarEncabezado = () => (
    <View style={styles.row}>
      <Text style={[styles.cell, styles.encabezado]}>Nombre Torta</Text>
      <Text style={[styles.cell, styles.encabezado]}>Descripción Torta</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Lista de Tortas</Text>
      {renderizarEncabezado()}
      <FlatList
        data={tortas}
        renderItem={renderizarItem}
        keyExtractor={(item) => item?.ID_TORTA?.toString()}
      />
      <TouchableOpacity style={styles.addButton} onPress={() => setModalVisible(true)}>
        <Text style={styles.addButtonText}>Agregar</Text>
      </TouchableOpacity>

      <Modal visible={modalVisible} animationType="slide">
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>{editMode ? 'Editar Torta' : 'Agregar Torta'}</Text>
            <TextInput
              style={styles.input}
              placeholder="Nombre"
              value={nombre}
              onChangeText={(text) => setNombre(text)}
            />
            <TextInput
              style={styles.input}
              placeholder="Descripción"
              value={descripcion}
              onChangeText={(text) => setDescripcion(text)}
            />
            <View style={styles.modalButtonContainer}>
              {showDeleteButton && (
                <TouchableOpacity style={styles.deleteButton} onPress={() => handleEliminarTorta(editTortaId)}>
                  <Text style={styles.deleteButtonText}>Eliminar</Text>
                </TouchableOpacity>
              )}
              <Button
                title={editMode ? 'Editar' : 'Agregar'}
                onPress={editMode ? handleEditarTorta : handleAgregarTorta}
              />
              <Button title="Cancelar" onPress={closeModal} color="#ccc" />
            </View>
          </View>
        </View>
      </Modal>
    </View>
  );
};

export default TortasScreen;
