import React, { useState, useEffect } from 'react';
import { View, FlatList, Text, TouchableOpacity, Modal, Image } from 'react-native';
import styles from '../styles/styles';

// Define un componente de tarjeta para mostrar cada receta
const RecetaCard = ({ receta, onEdit, onDelete, onExpand }) => {
  return (
    <TouchableOpacity
      style={styles.card}
      onPress={() => onExpand(receta)}
      onLongPress={() => onDelete(receta.idTorta)}
    >
      <Text style={styles.cardTitle}>Torta: {receta.idTorta}</Text>
      {/* Muestra una imagen para la foto de la torta */}
      <View style={styles.imageContainer}>
        <Image source={require('../path/to/your/image.jpg')} style={styles.image} />
      </View>
      {/* Muestra el listado de ingredientes solo si está expandido */}
      {receta.isExpanded && (
        <View style={styles.ingredientsContainer}>
          {receta.ingredientes.map((ingrediente, index) => (
            <View key={index} style={styles.ingredientContainer}>
              <Text style={styles.cardSubtitle}>Ingrediente: {ingrediente.idIngrediente}</Text>
              <Text style={styles.cardValue}>Cantidad: {ingrediente.cantidad}</Text>
            </View>
          ))}
        </View>
      )}
    </TouchableOpacity>
  );
};

const RecetaScreen2 = () => {
  const [recetas, setRecetas] = useState([]);
  const [modalVisible, setModalVisible] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editRecetaId, setEditRecetaId] = useState('');
  const [idTorta, setIdTorta] = useState('');
  const [idIngrediente, setIdIngrediente] = useState('');
  const [cantidadIngrediente, setCantidadIngrediente] = useState('');
  const [showDeleteButton, setShowDeleteButton] = useState(false);
  const [expandedReceta, setExpandedReceta] = useState(null);

  useEffect(() => {
    obtenerRecetas();
  }, []);

  const obtenerRecetas = async () => {
    // Simula la obtención de datos de recetas
    const data = [
      {
        idTorta: 'Torta 1',
        isExpanded: false,
        ingredientes: [
          { idIngrediente: 'Ingrediente 1', cantidad: '100g' },
          { idIngrediente: 'Ingrediente 2', cantidad: '100g' }
        ]
      },
      {
        idTorta: 'Torta 2',
        isExpanded: false,
        ingredientes: [
          { idIngrediente: 'Ingrediente 2', cantidad: '200g' }
        ]
      },
      // ...
    ];
    setRecetas(data);
  };

  const handleOpenModal = (recetaId) => {
    // Aquí puedes abrir el modal de edición de la receta como lo hacías antes
    // Recuerda que el código de edición de receta debería ser similar al anterior
  };

  const handleExpandCard = (receta) => {
    setExpandedReceta((prevExpanded) => ({
      ...prevExpanded,
      [receta.idTorta]: !prevExpanded[receta.idTorta],
    }));
  };

  const handleCloseExpandedCard = () => {
    setExpandedReceta(null);
  };

  const renderizarItem = ({ item }) => (
    <RecetaCard
      receta={item}
      onEdit={handleOpenModal}
      onExpand={handleExpandCard}
      onDelete={handleDeleteReceta} // Reemplaza handleDeleteReceta con tu lógica de eliminación
    />
  );

  // Resto de tu código para la función de eliminar receta y el modal de edición

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Lista de Recetas</Text>
      <FlatList
        data={recetas}
        renderItem={renderizarItem}
        keyExtractor={(item) => item.idTorta} // Usa el idTorta como clave
      />
      <TouchableOpacity style={styles.addButton} onPress={() => setModalVisible(true)}>
        <Text style={styles.addButtonText}>Agregar</Text>
      </TouchableOpacity>

      {expandedReceta && (
        <Modal visible={true} animationType="slide">
          {/* Contenido de la tarjeta expandida */}
          <View style={styles.expandedCardContainer}>
            <Text style={styles.expandedCardTitle}>Torta: {expandedReceta.idTorta}</Text>
            
            <View style={styles.ingredientsContainer}>
              {expandedReceta.ingredientes.map((ingrediente, index) => (
                <View key={index} style={styles.ingredientContainer}>
                  <Text style={styles.cardSubtitle}>Ingrediente: {ingrediente.idIngrediente}</Text>
                  <Text style={styles.cardValue}>Cantidad: {ingrediente.cantidad}</Text>
                </View>
              ))}
            </View>
            
            <TouchableOpacity style={styles.closeButton} onPress={handleCloseExpandedCard}>
              <Text style={styles.closeButtonText}>Cerrar</Text>
            </TouchableOpacity>
          </View>
        </Modal>
      )}
      
      {/* Resto de tu código para el modal de edición y adición de receta */}
    </View>
  );
};

export default RecetaScreen2;
