import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Text } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

import HomeScreen from '../views/HomeScreen';

import IngredientesScreen from '../views/IngredientesScreen';
import TortasScreen from '../views/TortasScreen';

const Stack = createNativeStackNavigator();

const AppNavigator = () => {
  const [isExpanded, setExpanded] = useState(false);
  const sidebarWidth = isExpanded ? 250 : 60;
  const arrowIcon = isExpanded ? 'chevron-left' : 'chevron-right';

  const toggleMenu = () => {
    setExpanded(!isExpanded);
  };

  return (
    <NavigationContainer>
      <View style={styles.container}>
        <TouchableOpacity
          style={[styles.sidebar, { width: sidebarWidth }]}
          onPress={toggleMenu}
          activeOpacity={0.7}
        >
          <Text style={styles.logo}>@CostoSmart</Text>
          {isExpanded && (
            <View style={styles.menuItems}>
              <TouchableOpacity
                onPress={() => console.log('Dashboard clicked')}
              >
                <Text style={styles.menuItem}>Dashboard</Text>
              </TouchableOpacity>
              <TouchableOpacity
                onPress={() => console.log('Ingredientes clicked')}
              >
                <Text style={styles.menuItem}>Ingredientes</Text>
              </TouchableOpacity>
              <TouchableOpacity onPress={() => console.log('Tortas clicked')}>
                <Text style={styles.menuItem}>Tortas</Text>
              </TouchableOpacity>
              <TouchableOpacity onPress={() => console.log('Cuenta clicked')}>
                <Text style={styles.menuItem}>Cuenta</Text>
              </TouchableOpacity>
            </View>
          )}
          <View style={styles.separator} />
          <View style={styles.bottomMenu}>
            <TouchableOpacity onPress={() => console.log('Settings clicked')}>
              <Text style={styles.bottomMenuItem}>Settings</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => console.log('Ayuda clicked')}>
              <Text style={styles.bottomMenuItem}>Ayuda</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => console.log('Contactanos clicked')}
            >
              <Text style={styles.bottomMenuItem}>Contactanos</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => console.log('Cerrar Sesion clicked')}
            >
              <Text style={styles.bottomMenuItem}>Cerrar Sesi√≥n</Text>
            </TouchableOpacity>
          </View>
        </TouchableOpacity>
        <View style={styles.content}>
          <Stack.Navigator initialRouteName="Home">
            <Stack.Screen name="Home" component={HomeScreen} />
          
            <Stack.Screen
              name="Ingredientes"
              component={IngredientScreen}
            />
            <Stack.Screen name="Tortas" component={TortasScreen} />
          </Stack.Navigator>
        </View>
      </View>
    </NavigationContainer>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  sidebar: {
    backgroundColor: '#333',
    paddingTop: 40,
    alignItems: 'center',
    justifyContent: 'flex-start',
    zIndex: 1,
  },
  logo: {
    fontSize: 20,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 20,
  },
  menuItems: {
    marginTop: 20,
  },
  menuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 10,
  },
  separator: {
    height: 1,
    backgroundColor: 'white',
    marginVertical: 20,
    width: '80%',
  },
  bottomMenu: {
    paddingBottom: 20,
  },
  bottomMenuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 10,
  },
  content: {
    flex: 1,
    backgroundColor: '#fff',
    justifyContent: 'center',
    alignItems: 'center',
  },
  screenTitle: {
    fontSize: 24,
    fontWeight: 'bold',
  },
});

export default AppNavigator;
