import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity } from 'react-native';
import { agregarIngrediente } from '../controllers/ingredientController';
import styles from '../styles/styles';

const NewIngredientScreen = ({ closeModal }) => {
  const [nombre, setNombre] = useState('');
  const [unidadMedida, setUnidadMedida] = useState('');
  const [tamanoPaquete, setTamanoPaquete] = useState('');
  const [costo, setCosto] = useState('');
  const [stock, setStock] = useState('');

  const handleAgregar = async () => {
    const ingredientData = {
      nombre,
      unidad_Medida: unidadMedida,
      tamano_Paquete: tamanoPaquete,
      costo,
      CantidadStock: stock,
    };

    try {
      await agregarIngrediente(ingredientData);
      console.log('Ingrediente agregado exitosamente');
      closeModal();
    } catch (error) {
      console.error('Error al agregar el ingrediente:', error);
      // Manejar el error aquí
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Nuevo Ingrediente</Text>
      <TextInput
        style={styles.input}
        placeholder="Nombre"
        value={nombre}
        onChangeText={setNombre}
      />
      <TextInput
        style={styles.input}
        placeholder="Unidad de Medida"
        value={unidadMedida}
        onChangeText={setUnidadMedida}
      />
      <TextInput
        style={styles.input}
        placeholder="Tamaño de Paquete"
        value={tamanoPaquete}
        onChangeText={setTamanoPaquete}
      />
      <TextInput
        style={styles.input}
        placeholder="Costo"
        value={costo}
        onChangeText={setCosto}
      />
      <TextInput
        style={styles.input}
        placeholder="Stock"
        value={stock}
        onChangeText={setStock}
      />
      <TouchableOpacity style={styles.button} onPress={handleAgregar}>
        <Text style={styles.buttonText}>Agregar</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={closeModal}>
        <Text style={styles.buttonText}>Cancelar</Text>
      </TouchableOpacity>
    </View>
  );
};

export default NewIngredientScreen;
