import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet } from 'react-native';
import axios from 'axios';

const IngredientesScreen = () => {
  const [ingredientes, setIngredientes] = useState([]);

  const fetchIngredientes = async () => {
    try {
      const response = await axios.get('http://localhost:3000/ingredientes');
      console.log(response.data); // Imprime los datos en la consola
      setIngredientes(response.data);
    } catch (error) {
      console.error('Error al obtener los datos:', error);
    }
  };


  useEffect(() => {
    fetchIngredientes();
  }, []);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Listado de Ingredientes</Text>
      <View style={styles.table}>
        <View style={styles.row}>
          <Text style={styles.header}>Ingrediente</Text>
          <Text style={styles.header}>ID</Text>
          <Text style={styles.header}>Unidad de Medida</Text>
          <Text style={styles.header}>Tama単o del Paquete</Text>
          <Text style={styles.header}>Costo</Text>
        </View>
        {ingredientes.map(ingrediente => (
          <View style={styles.row} key={ingrediente.ID}>
            <Text style={styles.cell}>{ingrediente.Ingrediente}</Text>
            <Text style={styles.cell}>{ingrediente.ID}</Text>
            <Text style={styles.cell}>{ingrediente.Unidad_de_Medida}</Text>
            <Text style={styles.cell}>{ingrediente.Tama単o_del_Paquete}</Text>
            <Text style={styles.cell}>{ingrediente.Costo}</Text>
          </View>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  table: {
    width: '100%',
  },
  row: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 10,
  },
  header: {
    fontWeight: 'bold',
    flex: 1,
  },
  cell: {
    flex: 1,
  },
});

export default IngredientesScreen;
