import React, { useState } from 'react';
import { View, TouchableOpacity, StyleSheet, Text, Animated } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { createDrawerNavigator } from '@react-navigation/drawer';

import HomeScreen from '../views/homeScreen';
import IngredientListScreen from '../views/IngredientListScreen';
import TortasScreen from '../views/TortasScreen';

const Drawer = createDrawerNavigator();

const AppNavigator = () => {
  const [isExpanded, setExpanded] = useState(false);
  const navigation = useNavigation();
  const sidebarWidth = isExpanded ? 250 : 60;
  const arrowIcon = isExpanded ? 'left' : 'right';

  const toggleMenu = () => {
    setExpanded(!isExpanded);
  };

  const sidebarWidthAnimation = new Animated.Value(sidebarWidth);

  const animateSidebar = () => {
    Animated.timing(sidebarWidthAnimation, {
      toValue: isExpanded ? 250 : 60,
      duration: 300,
      useNativeDriver: false,
    }).start();
  };

  return (
    <View style={styles.container}>
      <Animated.View style={[styles.sidebar, { width: sidebarWidthAnimation }]}>
        <Text style={styles.logo}>@CostoSmart</Text>
        {isExpanded && (
          <View style={styles.menuItems}>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Home');
              }}
            >
              <Text style={styles.menuItem}>Overview</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('IngredientStack');
              }}
            >
              <Text style={styles.menuItem}>Ingredientes</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setExpanded(false);
                navigation.navigate('Tortas');
              }}
            >
              <Text style={styles.menuItem}>Tortas</Text>
            </TouchableOpacity>
          </View>
        )}
        <TouchableOpacity style={styles.arrowButton} onPress={toggleMenu}>
          <Text style={styles.arrowIcon}>{arrowIcon}</Text>
        </TouchableOpacity>
      </Animated.View>
      <View style={styles.content}>
        <Drawer.Navigator
          initialRouteName="Home"
          drawerStyle={styles.drawerStyle}
          drawerContentOptions={{
            labelStyle: styles.drawerLabel,
          }}
        >
          <Drawer.Screen name="Home" component={HomeScreen} />
          <Drawer.Screen name="IngredientStack" component={IngredientListScreen} />
          <Drawer.Screen name="Tortas" component={TortasScreen} />
        </Drawer.Navigator>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  sidebar: {
    backgroundColor: 'black',
    paddingTop: 40,
    alignItems: 'center',
    justifyContent: 'flex-start',
    zIndex: 1,
  },
  logo: {
    fontSize: 20,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 20,
  },
  menuItems: {
    marginTop: 20,
  },
  menuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'white',
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  arrowButton: {
    position: 'absolute',
    bottom: 20,
    right: 10,
  },
  arrowIcon: {
    fontSize: 14,
    color: 'white',
    transform: [{ rotate: '180deg' }],
  },
  content: {
    flex: 1,
  },
  drawerStyle: {
    backgroundColor: 'white',
    width: '100%',
  },
  drawerLabel: {
    fontFamily: 'Source Sans 3',
    color: 'white',
  },
});

export default AppNavigator;
