import React, { useState } from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { Ionicons } from '@expo/vector-icons';
import { createStackNavigator } from '@react-navigation/stack';
import { View, TouchableOpacity, StyleSheet, Text } from 'react-native';

import HomeScreen from '../views/homeScreen';
import IngredientListScreen from '../views/IngredientListScreen';
import TortasScreen from '../views/TortasScreen';
import NewIngredientScreen from '../views/NewIngredientScreen';

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

const IngredientStack = () => (
  <Stack.Navigator>
    <Stack.Screen
      name="IngredientList"
      component={IngredientListScreen}
      options={{ headerShown: false }}
    />
    <Stack.Screen
      name="NewIngredient"
      component={NewIngredientScreen}
      options={{ title: 'Nuevo Ingrediente' }}
    />
  </Stack.Navigator>
);

const TabNavigator = () => {
  const [isExpanded, setExpanded] = useState(false);

  const toggleMenu = () => {
    setExpanded(!isExpanded);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        style={[styles.menu, isExpanded ? styles.menuExpanded : null]}
        onPress={toggleMenu}
        activeOpacity={0.7}
      >
        <Ionicons name={isExpanded ? 'close' : 'menu'} size={30} color="black" />
        {isExpanded && (
          <View style={styles.menuItems}>
            <Text style={styles.menuItem}>Overview</Text>
            <Text style={styles.menuItem}>Summary</Text>
            <Text style={styles.menuItem}>Tortas</Text>
            <Text style={styles.menuItem}>Recetas</Text>
            <Text style={styles.menuItem}>Cuenta</Text>
          </View>
        )}
      </TouchableOpacity>
      <View style={styles.content}>
        <Tab.Navigator
          tabBarOptions={{
            activeTintColor: '#86C997',
            inactiveTintColor: 'gray',
            labelStyle: {
              fontSize: 12,
              fontWeight: 'bold',
            },
            style: {
              backgroundColor: '#86C997',
              borderTopWidth: 1,
              borderTopColor: '#ccc',
            },
            showIcon: true,
            showLabel: true,
            tabStyle: {
              flexDirection: 'row',
            },
            indicatorStyle: {
              backgroundColor: '#86C997',
            },
          }}
          initialRouteName="Home"
        >
          <Tab.Screen
            name="IngredientStack"
            component={IngredientStack}
            options={({ route }) => ({
              tabBarLabel: isExpanded && route.name === 'IngredientStack' ? 'Ingredientes' : null,
              tabBarIcon: ({ color, size }) => (
                <Ionicons name="list" size={size} color={color} />
              ),
            })}
          />
          <Tab.Screen
            name="Home"
            component={HomeScreen}
            options={({ route }) => ({
              tabBarLabel: isExpanded && route.name === 'Home' ? 'Inicio' : null,
              tabBarIcon: ({ color, size }) => (
                <Ionicons name="home" size={size} color={color} />
              ),
            })}
          />
          <Tab.Screen
            name="Tortas"
            component={TortasScreen}
            options={({ route }) => ({
              tabBarLabel: isExpanded && route.name === 'Tortas' ? 'Tortas' : null,
              tabBarIcon: ({ color, size }) => (
                <Ionicons name="cake" size={size} color={color} />
              ),
            })}
          />
        </Tab.Navigator>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'row',
  },
  menu: {
    width: 60,
    backgroundColor: '#86C997',
    alignItems: 'center',
    justifyContent: 'center',
    transitionProperty: 'width',
    transitionDuration: '0.3s',
    zIndex: 1,
  },
  menuExpanded: {
    width: 120,
  },
  menuItems: {
    marginTop: 10,
  },
  menuItem: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'black',
    marginBottom: 10,
  },
  content: {
    flex: 1,
    backgroundColor: 'white', // Agregu√© un color de fondo para que se muestre el contenido
  },
});

export default TabNavigator;
