import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, Picker, TouchableOpacity } from 'react-native';
import { fetchIngredientesMenosStock,fetchIngredientes  } from '../controllers/IngredientController';
import { obtenerCantidadVentas, registrarVenta } from '../controllers/VentaController';
import { fetchListaPrecios } from '../controllers/ListaPreciosController';
import styles from '../styles/styles';




const HomeScreen = () => {
  const [listaPrecios, setListaPrecios] = useState([]);
  const [ingredientesFaltantes, setIngredientesFaltantes] = useState([]);
  const [selectedTorta, setSelectedTorta] = useState(null);
  const [mensaje, setMensaje] = useState('');
  const [selectedTortaNombre, setSelectedTortaNombre] = useState(null);
  const [cantidadVentas, setCantidadVentas] = useState(0);
  const [errorMensaje, setErrorMensaje] = useState(null);

  useEffect(() => {
    obtenerIngredientesFaltantes();
    obtenerCantidadVentasOnStart();
    obtenerListaPrecios();
  }, []);

  const obtenerListaPrecios = async () => {
    try {
      const listaPreciosData = await fetchListaPrecios();
      setListaPrecios(listaPreciosData);
    } catch (error) {
      console.error('Error al obtener la lista de precios:', error);
    }
  };
  const obtenerIngredientesFaltantes = async () => {
    try {
      const ingredientesFaltantesData = await fetchIngredientesMenosStock();
      setIngredientesFaltantes(ingredientesFaltantesData);
    } catch (error) {
      console.error('Error al obtener los ingredientes con menos stock:', error);
    }
  };
 
  const obtenerCantidadVentasOnStart = async () => {
    try {
      const nuevaCantidadVentas = await obtenerCantidadVentas();
      setCantidadVentas(nuevaCantidadVentas);
    } catch (error) {
      console.error('Error al obtener la cantidad de ventas al inicio:', error);
    }
  };
  const verificarStockSuficiente = async (ingredientesSeleccionados) => {
    const ingredientes = await fetchIngredientes();
  
    for (const ingredienteSeleccionado of ingredientesSeleccionados) {
      const ingrediente = ingredientes.find((i) => i.id === ingredienteSeleccionado.id);
  
      if (!ingrediente || ingrediente.cantidadStock < ingredienteSeleccionado.cantidad) {
        return false; // Stock insuficiente
      }
    }
  
    return true; // Hay suficiente stock
  };

  const renderizarItem = ({ item }) => (
    <View style={styles.row}>
      <Text style={styles.cell}>{item.nombre}</Text>
      <Text style={styles.cell}>{item.CantidadStock}</Text>
    </View>
  );

  const renderizarEncabezado = () => (
    <View style={styles.row}>
      <Text style={[styles.cell, styles.encabezado]}>pruebaaaa</Text>
      <Text style={[styles.cell, styles.encabezado]}>Cantidad Stock</Text>
    </View>
  );

  const handleGenerarVenta = async () => {
    setErrorMensaje(''); // Limpia el mensaje de error
  
    if (selectedTorta) {
      try {
        // Obtiene los ingredientes de la torta seleccionada
        const ingredientes = obtenerIngredientesDeTorta(selectedTorta);
  
        // Verifica si hay stock suficiente para los ingredientes
        const stockSuficiente = await verificarStockSuficiente(ingredientes);
  
        if (!stockSuficiente) {
          setErrorMensaje('Falta stock para generar la venta');
          return;
        }
  
        // Realiza la venta
        await registrarVenta(selectedTorta);
        const nuevaCantidadVentas = await obtenerCantidadVentas();
        setCantidadVentas(nuevaCantidadVentas);
        setMensaje('Venta generada exitosamente');
      } catch (error) {
        console.error('Error al generar la venta:', error);
        setErrorMensaje('Error al generar la venta');
      }
    } else {
      setErrorMensaje('Selecciona una torta para vender');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>PRECIOS</Text>

      <View style={styles.cardsContainer}>
        <View style={styles.card}>
          <Text style={styles.cardTitle}>Ventas Totales</Text>
          <Text style={styles.cardSubtitle}>{cantidadVentas} cantidad de ordenes</Text>
          <Text style={styles.cardValue}>3</Text>
          <View style={styles.cardFooter}>
            <Text style={styles.cardFooterText}>+15%</Text>
            <Text style={styles.cardFooterTextRight}>14.4k esta semana</Text>
          </View>
        </View>

        <View style={styles.card}>
          <Text style={styles.cardTitle}>Generar Venta</Text>
          <Text style={styles.cardSubtitle}>+ en la semana</Text>
          <View style={styles.cardContent}>
            <View style={styles.pickerContainer}>
            <Picker
                style={styles.picker}
                selectedValue={selectedTorta}
                onValueChange={(itemValue, itemIndex) => {
                  setSelectedTorta(itemValue);
                  setSelectedTortaNombre(listaPrecios[itemIndex].nombre_torta);
                }}
              >
                <Picker.Item label="Seleccionar torta" value={null} />
                {listaPrecios.map((torta) => (
                  <Picker.Item key={torta.id_torta} label={torta.nombre_torta} value={torta.id_torta} />
                ))}
              </Picker>
            </View>
            <TouchableOpacity
              style={styles.venderButton}
              onPress={handleGenerarVenta}
              disabled={!selectedTorta}
            >
              <Text style={styles.venderButtonText}>Vender</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>

      <FlatList
        data={ingredientesFaltantes}
        renderItem={renderizarItem}
        ListHeaderComponent={renderizarEncabezado}
        keyExtractor={(item) => item.id.toString()}
      />

      {mensaje ? <Text>{mensaje}</Text> : null}
    </View>
  );
};

export default HomeScreen;
